let word = wordsarray[Math.floor(Math.random() * 6626)].toUpperCase()
console.log(word)


function inin(elem, iter){
    for(let r = 0; r < iter.length; r++){
        if(elem == iter[r]){  
            return(true)
        }
    }
    return(false)
}

let counter = 0

let alphvit = ['й', 'ц', 'у', 'к', 'е', 'н', 'г', 'ш', 'щ', 'з', 'х', 'ъ', 'ф', 'ы', 'в', 'а', 'п', 'р', 'о', 'л', 'д', 'ж', 'э', 'я', 'ч', 'с', 'м', 'и', 'т', 'ь', 'б', 'ю', 'ё']


function eventfun(){
    inp.value = inp.value.toUpperCase()
    if(inp.value.length >= 5){
        if(counter < 5){
            document.querySelector('.block').remove()
        }
        else{
            inp.remove()
        }
        let inpval = inp.value
        inp.value = ''
        let linediv = document.querySelector('.linediv' + counter)
        for(let i = 0;i < 5; i++){            
            let cell = document.createElement('div');
            cell.classList.add('cell')
            linediv.appendChild(cell)
            cell.innerHTML = inpval[i]
            
            if(inin(inpval[i],word) == true){
                if(inpval[i] in keyobject){
                    keyobject[inpval[i]].style.backgroundColor = 'yellow'
                }
                cell.style.backgroundColor = 'yellow'
            }
            else{
                if(inpval[i] in keyobject){
                    keyobject[inpval[i]].style.backgroundColor = 'red'
                }
            }
            if(inpval[i] == word[i]){
                cell.style.backgroundColor = 'green'
                if(inpval[i] in keyobject){
                    keyobject[inpval[i]].style.backgroundColor = 'green'
                delete keyobject[inpval[i]]
                }
            }
            
        }
       counter++
       if(inpval == word){
        let endpage = document.querySelector('.won')
            addword(endpage)
            document.querySelector('.contan').style.display = 'none'
            endpage.style.display = 'flex'
            
       }
       if(counter >= 6){
            let endpage = document.querySelector('.lose')
            addword(endpage)
            document.querySelector('.contan').style.display = 'none'
            endpage.style.display = 'flex'
            
       }
    }
}

let inp = document.querySelector('.inp')
inp.addEventListener('keyup', even =>{
    eventfun()
})


let keyarray = []
for(let i = 0; i < 33; i++){
    keyarray.push(document.querySelector('.' + alphvit[i] + (i + 1)))
    keyarray[i].addEventListener('click', even =>{
        inp.value += even.target.childNodes[0].data;
        eventfun()
    })
}

let keyobject = {}
function arraytoobjakt(){
    for(let i = 0; i < 33; i++){
        keyobject[alphvit[i].toUpperCase()] = keyarray[i]
    }
}
arraytoobjakt()

function addword(arg){
    let pdocvord = document.createElement('p')
    pdocvord.textContent = word
    pdocvord.classList.add('pfin')
    arg.appendChild(pdocvord)
}